mapper.insert:

insert into account (id, account, password, 
  power, create_time, update_time, 
  create_account, update_account, is_del
  )
values (#{id,jdbcType=INTEGER}, #{account,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{power,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, 
  #{createAccount,jdbcType=INTEGER}, #{updateAccount,jdbcType=INTEGER}, #{isDel,jdbcType=INTEGER}
  )

mapper.select:
select

from account

  
    
      account like '${%param.account}%'
    
    
      update_time between ${param.startUpdateTime} and ${param.endUpdateTime}
    
    
      is_del = ${param.isDel}
    
  
  
    limit ${param.offset}, ${param.pageSize}
